import React, { useState, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../../UserContext';
import './RequestPage.css';

const RequestPage = () => {
  const { user } = useContext(UserContext);
  const [date, setDate] = useState('');
  const [topic, setTopic] = useState('');
  const [attendees, setAttendees] = useState('');
  const [venue, setVenue] = useState('');
  const [hodEmail, setHodEmail] = useState('');
  const [resourcePerson, setResourcePerson] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const requestData = {
      date,
      topic,
      attendees,
      venue,
      hodEmail,
      resourcePerson,
      description,
      facultyId: user.id,
      facultyEmail: user.email,
    };

    try {
      const response = await axios.post('/lecture-api/submit-request', requestData);

      if (response.data.success) {
        alert('Request submitted successfully!');
      } else {
        setError(response.data.message || 'Failed to submit request');
      }
    } catch (error) {
      setError(error.response ? error.response.data.message : 'Request submission failed');
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  if (user.role !== 'faculty') {
    return <div>Only faculty can request guest lectures.</div>;
  }

  return (
    <form onSubmit={handleSubmit} className="request-form">
      <h2>Request Guest Lecture</h2>
      {error && <div className="error">{error}</div>}
      <input 
        type="date" 
        value={date} 
        onChange={(e) => setDate(e.target.value)} 
        required 
      />
      <input 
        type="text" 
        placeholder="Topic" 
        value={topic} 
        onChange={(e) => setTopic(e.target.value)} 
        required 
      />
      <input 
        type="text" 
        placeholder="Attendees" 
        value={attendees} 
        onChange={(e) => setAttendees(e.target.value)} 
        required 
      />
      <input 
        type="text" 
        placeholder="Venue" 
        value={venue} 
        onChange={(e) => setVenue(e.target.value)} 
        required 
      />
      <input 
        type="email" 
        placeholder="HOD Email" 
        value={hodEmail} 
        onChange={(e) => setHodEmail(e.target.value)} 
        required 
      />
      <input 
        type="text" 
        placeholder="Resource Person" 
        value={resourcePerson} 
        onChange={(e) => setResourcePerson(e.target.value)} 
        required 
      />
      <textarea 
        placeholder="Description" 
        value={description} 
        onChange={(e) => setDescription(e.target.value)} 
        required 
      />
      <button type="submit">Submit Request</button>
    </form>
  );
};

export default RequestPage;
