import React, { useContext, useEffect, useState } from 'react';
import { UserContext } from '../../UserContext';
import './FacultyProfile.css';
import Logout from '../Logout/Logout';
import axios from 'axios';

const FacultyProfile = () => {
  const { user, loading } = useContext(UserContext);
  const [facultyData, setFacultyData] = useState(null);

  useEffect(() => {
    const fetchFacultyData = async () => {
      try {
        if (user) {
          const response = await axios.get(`http://localhost:4000/faculty-api/profile/${user._id}`);
          setFacultyData(response.data);
        }
      } catch (error) {
        console.error('Failed to fetch faculty data', error);
      }
    };

    if (user) {
      fetchFacultyData();
    }
  }, [user]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <div>Please log in to view your profile.</div>;
  }

  if (!facultyData) {
    return <div>Loading faculty data...</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>Faculty Profile</h2>
        <nav className="profile-nav">
          <ul>
            <li><a href="/request-guest-lecture">Request Guest Lecture</a></li>
            <li><a href="/dashboard">Dashboard</a></li>
          </ul>
        </nav>
        <Logout />
      </div>
      
      <div className="card">
        <h3>Welcome, {facultyData.name}</h3>
        <p>Here you can manage your guest lecture requests and view your dashboard.</p>
      </div>
      
      <div className="card">
        <h3>Quick Actions</h3>
        <ul>
          <li><a href="/request-guest-lecture">Submit a new guest lecture request</a></li>
          <li><a href="/dashboard">View your dashboard</a></li>
        </ul>
      </div>
    </div>
  );
};

export default FacultyProfile;
